import React, { Component } from 'react';
import './App.css';
import Person from './Person/person';

class App extends Component {
  state = {
    persons: [
      { name: 'John', age: 37 },
      { name: 'Peter', age: 88 },
      { name: 'Josephine', age: 56 }
    ]
  }

  switchNameHandler = (newName) => {
    //console.log('I was clicked');
    // Don't do this: this.state.persons[0].name = "Omar";
    this.setState({
      persons: [
        { name: newName, age: 24 },
        { name: 'Peter', age: 88 },
        { name: 'Josephine', age: 56 }
      ]
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Hey baby, I'm a React App!</h1>
        <p>And you're still learning!</p>
        <button onClick={this.switchNameHandler.bind(this, "Ramon")}>Switch Name</button>
        <Person
          name={this.state.persons[0].name}
          age={this.state.persons[0].age}/>
        <Person
          name={this.state.persons[1].name}
          age={this.state.persons[1].age}
          click={this.switchNameHandler.bind(this, "John")}> My hobbies include: Playing bass guitar </Person>
        <Person
          name={this.state.persons[2].name}
          age={this.state.persons[2].age}/>
      </div>
    );
  }
}

export default App;
